formulas.expression:
IF(
    OR(
        ISPICKVAL({!$Record.Campaign.Market_Unit__c}, "DFJ_DK"),
        ISPICKVAL({!$Record.Campaign.Market_Unit__c}, "Testamente_DK")
    ),
    /* ----------------
       DK formatting: (+45) + phone digits without any leading 45 / +45 
       ---------------- */
    "(+45)" & 
    IF(
        BEGINS(SUBSTITUTE({!$Record.Phone}, " ", ""), "+45"),
        /* remove the leading +45 */
        RIGHT(
            SUBSTITUTE({!$Record.Phone}, " ", ""),
            LEN(SUBSTITUTE({!$Record.Phone}, " ", "")) - 3
        ),
        IF(
            BEGINS(SUBSTITUTE({!$Record.Phone}, " ", ""), "45"),
            /* remove the leading 45 */
            RIGHT(
                SUBSTITUTE({!$Record.Phone}, " ", ""),
                LEN(SUBSTITUTE({!$Record.Phone}, " ", "")) - 2
            ),
            /* fallback - just strip spaces and prepend (+45) */
            SUBSTITUTE({!$Record.Phone}, " ", "")
        )
    ),
    IF(
        ISPICKVAL({!$Record.Campaign.Market_Unit__c}, "FA_SE"),
        /* ----------------
           SE formatting: 46 + phone digits (no plus sign)
           ---------------- */
        IF(
            BEGINS(SUBSTITUTE({!$Record.Phone}, " ", ""), "+46"),
            /* remove the leading '+' but keep '46' */
            "46" & RIGHT(
                      SUBSTITUTE({!$Record.Phone}, " ", ""),
                      LEN(SUBSTITUTE({!$Record.Phone}, " ", "")) - 3
                  ),
            IF(
                BEGINS(SUBSTITUTE({!$Record.Phone}, " ", ""), "46"),
                /* already starts with 46 - just strip spaces */
                SUBSTITUTE({!$Record.Phone}, " ", ""),
                /* fallback - add 46 plus the rest */
                "46" & SUBSTITUTE({!$Record.Phone}, " ", "")
            )
        ),
        /* ----------------
           For all other market units,
           just remove spaces (no country code changes).
           ---------------- */
        SUBSTITUTE({!$Record.Phone}, " ", "")
    )
)
