start.filterFormula:
OR(
  /* Created + any present */
  AND(
    ISBLANK({!$Record__Prior.Id}),
    OR(
      NOT(ISBLANK({!$Record.Email})),
      NOT(ISBLANK({!$Record.Phone})),
      NOT(ISBLANK({!$Record.Email2__c})),
      NOT(ISBLANK({!$Record.Phone2__c}))
    )
  ),
  /* Updated + any changed */
  AND(
    NOT(ISBLANK({!$Record__Prior.Id})),
    OR(
      {!$Record.Email} <> {!$Record__Prior.Email},
      {!$Record.Phone} <> {!$Record__Prior.Phone},
      {!$Record.Email2__c} <> {!$Record__Prior.Email2__c},
      {!$Record.Phone2__c} <> {!$Record__Prior.Phone2__c}
    )
  )
)
