formulas.expression:
/* Thu/Fri  +2+2, Sat  +2+1, SunWed  +2*/
IF(
  OR(
    WEEKDAY({!$Flow.CurrentDate}) = 5,   /* Thursday */
    WEEKDAY({!$Flow.CurrentDate}) = 6    /* Friday  */
  ),
  {!$Flow.CurrentDate} + 2 + 2,
  IF(
    WEEKDAY({!$Flow.CurrentDate}) = 7,   /* Saturday */
    {!$Flow.CurrentDate} + 2 + 1,
    {!$Flow.CurrentDate} + 2 /* Sun-Wed */
  )
)

