ValidationRule.errorConditionFormula:
AND(
  ISPICKVAL(Attendance_Status__c, "Absent"),
  OR(
    /* Category must be filled */
    ISBLANK(TEXT(Absence_Category__c)),
    /* Compensation must be filled unless category is Default Work Schedule */
    AND(
      NOT(ISPICKVAL(Absence_Category__c, "Default Work Schedule")),
      ISBLANK(TEXT(Compensation__c))
    )
  )
)
