ValidationRule.errorConditionFormula:
AND(
  RecordType.DeveloperName <> "Host",
  OR(
    /* If Market_Unit is DFJ_DK/Testamente_DK but RecordType is not DFJ_DK */
    AND(
      OR(
        ISPICKVAL(Market_Unit__c, "DFJ_DK"),
        ISPICKVAL(Market_Unit__c, "Testamente_DK")
      ),
      RecordType.DeveloperName <> "DFJ_DK"
    ),

    /* If Market_Unit is FA_SE/Testamente_SV but RecordType is not FA_SE */
    AND(
      OR(
        ISPICKVAL(Market_Unit__c, "FA_SE"),
        ISPICKVAL(Market_Unit__c, "Testamente_SV")
      ),
      RecordType.DeveloperName <> "FA_SE"
    ),

    /* If Market_Unit is Ireland but RecordType is not Ireland */
    AND(
      ISPICKVAL(Market_Unit__c, "Ireland"),
      RecordType.DeveloperName <> "Ireland"
    )
  )
)
