CustomField.formula:
IF(NOT(ISBLANK(Account__c)), "Account",
    IF(NOT(ISBLANK(Lead__c)), "Lead",
        IF(NOT(ISBLANK(Journal__c)), "Journal",
            IF(NOT(ISBLANK(Order__c)), "Order",
                IF(NOT(ISBLANK(Campaign__c)), "Campaign",
                    IF(NOT(ISBLANK(Contact__c)), "Contact", "")
                )
            )
        )
    )
)
