CustomField.formula:
OR(
  /* renewal next month, same year */
  AND(
    MONTH(TODAY()) <> 12,
    MONTH(DATEVALUE(Next_Renewal_Date__c)) = MONTH(TODAY()) + 1,
    YEAR(DATEVALUE(Next_Renewal_Date__c))  = YEAR(TODAY())
  ),
  /* renewal in January next year when today is December */
  AND(
    MONTH(TODAY()) = 12,
    MONTH(DATEVALUE(Next_Renewal_Date__c)) = 1,
    YEAR(DATEVALUE(Next_Renewal_Date__c))  = YEAR(TODAY()) + 1
  )
)
