CustomField.formula:
CASE(
  Target_Period__c ,

  /* Quarter → first day of quarter */
  "Quarter",
    DATE(
      Year__c,
      (Quarter_Number__c - 1) * 3 + 1,
      1
    ),

  /* Month → first day of month */
  "Month",
    DATE( Year__c, Month_Number__c, 1 ),

  /* Week → Monday of ISO week */
  "Week",
    DATE( Year__c, 1, 4 )
    - MOD( WEEKDAY( DATE( Year__c, 1, 4 ) ) + 5, 7 )
    + ( Week_Number__c - 1 ) * 7,

  /* Day → that day of the year */
  "Day",
    DATE( Year__c, 1, 1 ) + Day_Number__c - 1,

  /* default */
  NULL
)
